<?php	$server_link = '../';	$rel = $server_link;	$page = './admin_key.php';	include_once $server_link.'dao/connectDb.php';	include_once $server_link.'dao/mysqlUtils.php';	include_once $server_link.'dao/retrieveUtils.php';	include_once $server_link.'filtres/filtre.php';	include_once $server_link.'private_datas/filtre_fonc.php';	include_once $server_link.'utils/convertVars.php';		$usr = null;	$usr['adminkey'] = isset($_GET['adminkey']) ? $_GET['adminkey'] : null;	filtreAdmin($usr); // exit if incorrect		$dbVars = setDbVars();	dbConnect();		$req = "SELECT k.service_id, k.no, k.type, k.key, k.create_date, k.max_cpt, k.reset_time, k.commentaire FROM `".$dbVars['name']."`.`newCQ_key_cpt` k ORDER BY k.service_id, k.type, k.no;";	$res = query($req, $usr);	if($res != null && mysql_num_rows($res) != 0){		$htmlKeys = '<tr>			<th>type</th>			<th>service</th>			<th>no</th>			<th>key</th>			<th>create_date</th>			<th>max_cpt</th>			<th>reset_time</th>			<th>commentaire</th>		</tr>';		while($ret = mysql_fetch_array($res, MYSQL_ASSOC)){			$htmlKeys .= '<tr>				<td>'.$ret['type'].'</td>				<td>'.getServiceName($ret['service_id']).'</td>				<td>'.$ret['no'].'</td>				<td>'.$ret['key'].'</td>				<td>'.$ret['create_date'].'</td>				<td>'.$ret['max_cpt'].'</td>				<td>'.$ret['reset_time'].'</td>				<td>'.$ret['commentaire'].'</td>			</tr>';		}	}	else{		$htmlKeys = '<tr><td>Pas de cl√©s actives</td></tr>';	}			$htmlServices = '<tr>		<th>id</th>		<th>name</th>		<th>nbUsedKey</th>		<th>nbUsedAdminKey</th>	</tr>';	foreach(getServices() as $service){		$htmlServices .= '<tr>			<td>'.$service['id'].'</td>			<td>'.$service['name'].'</td>			<td>'.$service['nbUsedKey'].'</td>			<td>'.$service['nbUsedAdminKey'].'</td>		</tr>';	}		unset($req);	dbDisconnect();?><!doctype html><head>	<meta charset="UTF-8">	<meta http-equiv="X-UA-Compatible" content="IE=edge,chrome=1">		<title>Admin keys</title>	<meta name="description" content="">	<meta name="author" content="">		<meta name="viewport" content="width=device-width, initial-scale=1.0">	<style>	td, th{		border:solid 1px black;		text-align: center;	}	table{		border-collapse:collapse;	}	</style></head><body>	<?php		echo 'Service : '.$usr['noService'].'. '.getServiceName($usr['noService']).'<br/>';		echo '<table>'.$htmlKeys.'</table><br/>';		echo '<table>'.$htmlServices.'</table>';					?>	<a href="./?adminkey=<?php echo $_GET['adminkey']; ?>"><= retour index</a></body></html>