<?php	$rel = '../../';	include_once '../fonc/getCategory.php';	include_once '../fonc/postCategory.php';	include_once $rel.'filtres/filtre.php';	include_once $rel.'utils/json.php';	include_once $rel.'utils/secureVars.php';		if(isset($_POST['key']) || (isset($_GET['key']) && isset($_GET['meth']) && $_GET['meth'] == "post") ){		if(isset($_GET['meth']) && $_GET['meth'] == "post"){$par = $_GET;}		else{$par = $_POST;}				$usr = null;		$usr['key'] = safe_string(isset($par["key"]) ? $par["key"] : null);		filtre($usr); // exit if incorrect				if(isset($par['cat']) && $par['cat'] != ''){			echo apiNewCategory($usr, $par, $rel);		}		else{			echo createErrorJson($usr, 400); // paramètres incorrects		}	}	else if(isset($_GET['key'])){		$usr = null;		$usr['key'] = safe_string(isset($_GET["key"]) ? $_GET["key"] : null);		filtre($usr); // exit if incorrect				echo apiGetCategories($usr, $_GET, $rel);	}	else{		echo createErrorJson(null, 401); // clé de connexion incorrecte	}	?>