<?php// API : category.php:GET?key={key}[&p={no_category_page}]function apiGetCategories(&$usr, $params, $server_path){	$rel = isset($server_path) ? $server_path : '../../../';	include_once $rel.'services/get.php';	include_once $rel.'utils/json.php';	include_once $rel.'utils/secureVars.php';	include_once 'utils/extra_data.php';		saveExtraDatas($usr, $params);	$p = safe_int(isset($params["p"]) ? $params["p"] : '1');		$pages = getTotalCategoriesPages($usr);	if($p == null){$p = '1'; }	else if($p > $pages){$p = $pages;}		$categories_result = getCategories($usr, $p);		if($categories_result != null){		return createCategoriesJson($usr, $categories_result, $p, $pages);	} else{ return createErrorJson($usr, 404); } // pas de résultats}?>