<?php// API : selection.php:GET?key={key}[&p={no_selection_page}]function apiGetSelections(&$usr, $params, $server_path){	$rel = isset($server_path) ? $server_path : '../../../';	include_once $rel.'services/get.php';	include_once $rel.'utils/json.php';	include_once $rel.'utils/secureVars.php';	include_once 'utils/extra_data.php';		saveExtraDatas($usr, $params);	$p = safe_int(isset($params["p"]) ? $params["p"] : '1');		$pages = getTotalSelectionsPages($usr);	if($p == null){$p = '1'; }	else if($p > $pages){$p = $pages;}		$selections_result = getSelections($usr, $p);		if($selections_result != null){		return createSelectionsJson($usr, $selections_result, $p, $pages);	} else{ return createErrorJson($usr, 404); } // pas de résultats}?>