<?php// API : selection.php:GET?key={key}[&p={no_selection_page}]function apiGetSelections(&$usr, $params, $server_path){	$rel = isset($server_path) ? $server_path : '../../../';	include_once $rel.'services/get.php';	include_once $rel.'utils/json.php';	include_once $rel.'utils/secureVars.php';	include_once 'utils/extra_data.php';		saveExtraDatas($usr, $params);	$p = isset($params["p"]) ? ($params["p"] == 'all' ? $params["p"] : safe_int($params["p"])) : '1';		if($p == 'all'){ $pages = 1; }	else{		$pages = getTotalSelectionsPages($usr);		if($p > $pages){$p = $pages;}	}		$selections_result = getSelections($usr, $p);		if($selections_result != null){		if($p == 'all'){$p = 1;}		return createSelectionsJson($usr, $selections_result, $p, $pages);	} else{ return createErrorJson($usr, 404); } // pas de résultats}?>