<?php	include_once $rel.'globals/env.php';function query($req, $usr, $args = null){	$dbVars = setDbVars();	$env = setEnv();		$persistLog = isset($args['persistLog']) ? $args['persistLog'] : true;	$displayReq = isset($args['displayReq']) ? $args['displayReq'] : true;		if($env['logSQLreq'] == true  && $persistLog == true){		$log = "INSERT INTO `".$dbVars['name']."`.`newCQ_sqlreq_log` (`service_id`, `api_log_id`, `request`)VALUES ('".$usr['noService']."', '".$usr['logid']."', '".str_replace("'", "", $req)."');";		if($env['displaySQLlogreq'] == true){echo $log.'<br/><br/>';}		mysql_query($log);	}		if( ($env['displaySQLreq'] == true && $displayReq == true) || $env['displaySQLlogreq'] == true){echo $req.'<br/><br/>';}		return mysql_query($req);}function nullSafe($val){	if($val == null){return 'NULL';}	else{return "'".$val."'";}}?>