<?php	include_once $rel.'dao/connectDb.php';	include_once $rel.'globals/env.php';		function persistFatalErrorLog($usr, $msg, $connect = false){		if($connect == true){dbConnect();}		$dbVars = setDbVars(getStatus());		$log = "INSERT INTO `".$dbVars['DbName']."`.`".$dbVars['DbPrefix']."app_log` (`service_id`, `msg`)VALUES ('".$usr['noService']."', '".$msg."');";		mysql_query($log);		dbDisconnect();		exit();	}?>